import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Set environment variables for Vite to access
if (process.env.RAZORPAY_KEY_ID) {
  process.env.VITE_RAZORPAY_KEY_ID = process.env.RAZORPAY_KEY_ID;
}

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertContactInquirySchema, insertPaymentSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Contact form submission endpoint
  app.post("/api/contact", async (req, res) => {
    try {
      const validatedData = insertContactInquirySchema.parse(req.body);
      const inquiry = await storage.createContactInquiry(validatedData);
      res.json({ success: true, inquiry });
    } catch (error) {
      console.error("Error creating contact inquiry:", error);
      res.status(400).json({ 
        success: false, 
        error: error instanceof Error ? error.message : "Failed to submit inquiry" 
      });
    }
  });

  // Get all contact inquiries
  app.get("/api/contact", async (req, res) => {
    try {
      const inquiries = await storage.getAllContactInquiries();
      res.json({ success: true, inquiries });
    } catch (error) {
      console.error("Error fetching inquiries:", error);
      res.status(500).json({ 
        success: false, 
        error: "Failed to fetch inquiries" 
      });
    }
  });

  // Update contact inquiry status
  app.patch("/api/contact/:id/status", async (req, res) => {
    try {
      const { id } = req.params;
      const { status } = req.body;
      const inquiry = await storage.updateContactInquiryStatus(id, status);
      res.json({ success: true, inquiry });
    } catch (error) {
      console.error("Error updating inquiry status:", error);
      res.status(400).json({ 
        success: false, 
        error: "Failed to update inquiry status" 
      });
    }
  });

  // Create payment
  app.post("/api/payments", async (req, res) => {
    try {
      const validatedData = insertPaymentSchema.parse(req.body);
      const payment = await storage.createPayment(validatedData);
      res.json({ success: true, payment });
    } catch (error) {
      console.error("Error creating payment:", error);
      res.status(400).json({ 
        success: false, 
        error: error instanceof Error ? error.message : "Failed to create payment" 
      });
    }
  });

  // Get all payments
  app.get("/api/payments", async (req, res) => {
    try {
      const payments = await storage.getAllPayments();
      res.json({ success: true, payments });
    } catch (error) {
      console.error("Error fetching payments:", error);
      res.status(500).json({ 
        success: false, 
        error: "Failed to fetch payments" 
      });
    }
  });

  // Update payment
  app.patch("/api/payments/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const payment = await storage.updatePayment(id, req.body);
      res.json({ success: true, payment });
    } catch (error) {
      console.error("Error updating payment:", error);
      res.status(400).json({ 
        success: false, 
        error: "Failed to update payment" 
      });
    }
  });

  // Get admin stats
  app.get("/api/admin/stats", async (req, res) => {
    try {
      const stats = await storage.getStats();
      res.json({ success: true, stats });
    } catch (error) {
      console.error("Error fetching stats:", error);
      res.status(500).json({ 
        success: false, 
        error: "Failed to fetch stats" 
      });
    }
  });

  // Export all leads as CSV
  app.get("/api/admin/export/all", async (req, res) => {
    try {
      const [contacts, payments] = await Promise.all([
        storage.getAllContactInquiries(),
        storage.getAllPayments()
      ]);

      const allLeads = [
        ...contacts.map(c => ({
          type: 'Contact Form',
          id: c.id,
          name: c.name,
          email: c.email,
          phone: c.phone,
          service: c.service,
          message: c.message,
          status: c.status,
          amount: '',
          planName: '',
          category: '',
          createdAt: c.createdAt
        })),
        ...payments.map(p => ({
          type: 'Payment',
          id: p.id,
          name: p.name || '',
          email: p.email || '',
          phone: p.phone || '',
          service: p.category,
          message: '',
          status: p.status,
          amount: `₹${p.amount}`,
          planName: p.planName,
          category: p.category,
          createdAt: p.createdAt
        }))
      ].sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

      const csv = [
        'Type,ID,Name,Email,Phone,Service/Category,Plan Name,Amount,Status,Message,Created At',
        ...allLeads.map(lead => 
          `"${lead.type}","${lead.id}","${lead.name}","${lead.email}","${lead.phone}","${lead.service}","${lead.planName}","${lead.amount}","${lead.status}","${lead.message}","${new Date(lead.createdAt).toLocaleString()}"`
        )
      ].join('\n');

      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', 'attachment; filename=all-leads.csv');
      res.send(csv);
    } catch (error) {
      console.error("Error exporting data:", error);
      res.status(500).json({ 
        success: false, 
        error: "Failed to export data" 
      });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { type ContactInquiry, type InsertContactInquiry, contactInquiries, type Payment, type InsertPayment, payments } from "@shared/schema";
import { db } from "../db/index";
import { desc, eq } from "drizzle-orm";

export interface IStorage {
  createContactInquiry(inquiry: InsertContactInquiry): Promise<ContactInquiry>;
  getAllContactInquiries(): Promise<ContactInquiry[]>;
  updateContactInquiryStatus(id: string, status: string): Promise<ContactInquiry | undefined>;

  createPayment(payment: InsertPayment): Promise<Payment>;
  updatePayment(id: string, data: Partial<InsertPayment>): Promise<Payment | undefined>;
  getAllPayments(): Promise<Payment[]>;

  getStats(): Promise<{
    totalBookings: number;
    pending: number;
    contacted: number;
    completed: number;
    contactForms: number;
    totalPayments: number;
    revenue: number;
  }>;
}

export class DbStorage implements IStorage {
  async createContactInquiry(inquiry: InsertContactInquiry): Promise<ContactInquiry> {
    const [created] = await db.insert(contactInquiries).values(inquiry).returning();
    return created;
  }

  async getAllContactInquiries(): Promise<ContactInquiry[]> {
    return await db.select().from(contactInquiries).orderBy(desc(contactInquiries.createdAt));
  }

  async updateContactInquiryStatus(id: string, status: string): Promise<ContactInquiry | undefined> {
    const [updated] = await db
      .update(contactInquiries)
      .set({ status })
      .where(eq(contactInquiries.id, id))
      .returning();
    return updated;
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const [created] = await db.insert(payments).values(payment).returning();
    return created;
  }

  async updatePayment(id: string, data: Partial<InsertPayment>): Promise<Payment | undefined> {
    const [updated] = await db
      .update(payments)
      .set(data)
      .where(eq(payments.id, id))
      .returning();
    return updated;
  }

  async getAllPayments(): Promise<Payment[]> {
    return await db.select().from(payments).orderBy(desc(payments.createdAt));
  }

  async getStats() {
    const allPayments = await this.getAllPayments();
    const allContacts = await this.getAllContactInquiries();

    const completedPayments = allPayments.filter(p => p.status === 'completed');
    const pendingPayments = allPayments.filter(p => p.status === 'pending' || p.status === 'initiated');
    const contactedLeads = allContacts.filter(c => c.status === 'contacted');
    const completedLeads = allContacts.filter(c => c.status === 'completed');

    const revenue = completedPayments.reduce((sum, p) => sum + p.amount, 0);

    return {
      totalBookings: allPayments.length,
      pending: pendingPayments.length,
      contacted: contactedLeads.length,
      completed: completedLeads.length,
      contactForms: allContacts.length,
      totalPayments: allPayments.length,
      revenue: revenue,
    };
  }
}

export const storage = new DbStorage();

